$(function(){
	$("body").on("click",".popClose",function(){
		$(".dialogBox").hide();
	});

	$("body").on("click","#agreeAll",function(){
		var ck = $(this).prop("checked"); 
		if(ck){
			$(".chkBox input").prop("checked", true);
		}else{
			$(".chkBox input").prop("checked", false);
		}
	});

	$("body").on("click",".subTabs a",function(){
		$(".subTabs a").removeClass("on");
		$(this).addClass("on");
	});

	
});